import React, { useRef, useEffect, useState} from "react";
import actionTree from "../../data/actionTree.json";
import action from "../../data/action.json";
import paper from "../../data/paper.json";

const PaperList = (props) => {

  let query = null;
  let authorID = null;
  let citedBy = null;
  let startYear = null;
  let url = null;
  let searchedPapers = null;
  useEffect(() => {
    query = action['query'][props.selectedAction]
    authorID = action['authorID'][props.selectedAction]
    citedBy = action['citedBy'][props.selectedAction]
    startYear = action['startYear'][props.selectedAction]
    url = action['url'][props.selectedAction]
    searchedPapers = action['searched_papers'][props.selectedAction]
    console.log(searchedPapers)
  }, [props])


	return (
		<div className="paperListContainer">
      {
        // action description
        props.selectedAction != null &&
        <>
        <div className="actionDescription">
          <p className="title">Action Description</p>
          {query && <p className="actions">- Searched Query : {query}</p>}
          {authorID && <p className="actions">- Searched Author : {authorID}</p>}
          {citedBy && <p className="actions">- Searched Citation : {citedBy}</p>}
          {startYear && <p className="actions">- Searched Year : {startYear} ~</p>}
          {query && <p className="actions">- Searched Query : {query}</p>}

        </div>
        </>
      }

      {
        // paper list
        searchedPapers != null && 
        <>
        {
          searchedPapers.map(paper => (
            <>
            </>
          ))
        }
        </>
      }
    </div>
	);
};

export default PaperList;
