import React, { useRef, useEffect, useState} from "react";
import Tree from "react-d3-tree";
import orgChartJson from "../../data/treeData.json";
import { useCenteredTree } from "../../helpers";


const containerStyles = {
  width: "100vw",
  height: "100vh"
};

const renderForeignObjectNode = ({
  nodeDatum,
  toggleNode,
  foreignObjectProps
}) => (
  <g>
    <foreignObject {...foreignObjectProps}>
      <div style={{ border: "1px solid black", backgroundColor: "#dedede" }}>
        <h3 style={{ textAlign: "center" }}>{nodeDatum.name}</h3>
        {nodeDatum.children && (
          <button style={{ width: "100%" }} onClick={toggleNode}>
            {nodeDatum.__rd3t.collapsed ? "Expand" : "Collapse"}
          </button>
        )}
      </div>
    </foreignObject>
  </g>
);

const customPathFunction = (linkDatum, orientation) => {
  const { source, target } = linkDatum;
  return orientation === 'horizontal'
    ? `M${source.y},${source.x}
       C${source.y*(1/2)+target.y*(1/2)},${source.x}  
        ${source.y*(1/2)+target.y*(1/2)},${target.x}  
        ${target.y},${target.x}`
    : `M${source.x},${source.y}L${target.x},${target.y}`;
};

const TreeChart = (props) => {
  const [translate, setTranslate, containerRef] = useCenteredTree();
  const [scale, setScale] = useState(1); // New state for scale
  const nodeSize = { x: 1000, y: 500 };
  const foreignObjectProps = { width: 200, height: nodeSize.y, x: -100 };

  const handleNodeClick = (nodeData) => {
    // Calculate new translate values to center the clicked node
    const newTranslate = {
      x: -nodeData.x + containerRef.current.offsetWidth / 2,
      y: -nodeData.y + containerRef.current.offsetHeight / 2,
    };

    setTranslate(newTranslate);
    setScale(1.5); // Example scale value, adjust as needed
  };

  return (
    <div style={containerStyles} ref={containerRef}>
      <Tree
        data={orgChartJson[2]}
        translate={translate}
        scale={scale} // Use the scale state
        nodeSize={nodeSize}
        renderCustomNodeElement={(rd3tProps) =>
          renderForeignObjectNode({ ...rd3tProps, foreignObjectProps })
        }
        pathFunc={(linkData) => customPathFunction(linkData, 'horizontal')}
        orientation="horizontal"
        onNodeClick={handleNodeClick} // Attach the click handler
      />
    </div>
  );
};

export default TreeChart;
